import { createAction } from 'redux-actions';
import Types from 'constants/types';

export function setPlayingTitle(title){
  return createAction(Types.SET_PLAYING_TITLE)(title);
}

export function setPlayingArtist(artist){
  return createAction(Types.SET_PLAYING_ARTIST)(artist);
}

export function setPlayingShuffle(shuffle){
  return createAction(Types.SET_PLAYING_SHUFFLE)(shuffle);
}