import { createAction } from 'redux-actions';
import Types from 'constants/types';

export function setVideoSource(source){
  return createAction(Types.SET_VIDEO_SOURCE)(source);
}

export function setVideoCurrentTime(currentTime){
  return createAction(Types.SET_VIDEO_CURRENT_TIME)(currentTime);
}

export function setVideoDuration(duration){
  return createAction(Types.SET_VIDEO_DURATION)(duration);
}

export function setVideoComponent(video){
  return createAction(Types.SET_VIDEO_COMPONENT)(video);
}

export function setVideoRepeat(repeat){
  return createAction(Types.SET_VIDEO_REPEAT)(repeat);
}

export function stopVideo(){
  return createAction(Types.STOP_VIDEO)();
}

export function pauseVideo(){
  return createAction(Types.PAUSE_VIDEO)();
}

export function playVideo(){
  return createAction(Types.PLAY_VIDEO)();
}

export function toggleVideoPlay(){
  return createAction(Types.TOGGLE_VIDEO_PLAY)(); 
}
