import Types from 'constants/types';
const initialState = {
  loggedIn: false,
  login: {},
  register: {}
}

module.exports = (state=initialState, action) => {
  switch(action.type){
    case Types.SET_USER_OBJECT: {
      return Object.assign(
        {}, state, { login: action.payload, loggedIn: true }
      );
    }

    case Types.REMOVE_USER_OBJECT: {
      return Object.assign(
        {}, state, { login: {}, loggedIn: false }
      );
    }

    case Types.SET_LOGIN_ERROR: {
      const login = Object.assign(
        {}, state.login, { error: action.payload }
      );
      return Object.assign(
        {}, state, {login}
      );
    }

    case Types.SET_REGISTER_ERROR: {
      const register = Object.assign(
        {}, state.register, { error: action.payload }
      );
      return Object.assign(
        {}, state, {register}
      );
    }

    default:
      return state;
  }
}